        // Get character pixel at specific position
        public static bool GetCharPixel(char c, int x, int y)
        {
            if (!CharacterPatterns.ContainsKey(c))
            {
                // For unknown characters, return false (no pixel)
                return false;
            }

            var pattern = CharacterPatterns[c];
            if (y >= pattern.Length) return false;

            string line = pattern[y];
            if (x >= line.Length) return false;

            // Ensure we don't go out of bounds
            if (x < 0 || x >= line.Length) return false;

            return line[x] == '█';
        }

        // Check if character is supported
        public static bool IsSupported(char c)
        {
            return CharacterPatterns.ContainsKey(c);
        }
